import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");

  const expenseYearChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const expensesOfTheYear = props.items.filter(expense => expense.date.getFullYear.toString() === filteredYear)

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onExpenseYearChange={expenseYearChangeHandler}
        />
        {expensesOfTheYear.map((expense) => (
          <ExpenseItem
            // add the key to force React to only add 1 new element, not recreate all elements  
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
